import React, { Component} from "react";
import Home from "./home";
import Game from "./game";

class App extends Component {
    constructor() {
        super();
        this.state = {
            page: 'home',
            clientID: '',
            partyID: '',
            username: '',
        }
    }

    joinGame(isHost, username, partyCode) {
        let ws = new WebSocket("ws://localhost:8000");

        ws.onopen = () => {
            console.log("websocket client connected");
        };

        ws.onmessage = message => {
            const response = JSON.parse(message.data);

            // Connected to server
            if(response.method === "connect") {
                // Save generated client ID
                this.setState({
                    clientID: response.clientID
                })

                // Send the room connection request to the server
                if(isHost) {
                    // Host Party
                    ws.send(JSON.stringify({
                        method: 'hostParty',
                        clientID: response.clientID,
                        username: username
                    }))
                } else {
                    // Join Party
                    ws.send(JSON.stringify({
                        method: 'joinParty',
                        clientID: response.clientID,
                        partyCode: partyCode,
                        username: username
                    }))
                }

            }

            // Joined Game Party
            if(response.method === "partyConnect") {
                // Update State
                this.setState({
                    page: 'game',
                    clientID: response.clientID,
                    partyCode: response.partyCode,
                    username: username
                })
            }
        }
    }

    render() { 
        let content;
        if(this.state.page === 'home') {
            content = <Home joinGame={(partyCode, username) => this.joinGame(partyCode, username)} />
        } else if(this.state.page === 'game') {
            content = <Game clientID={this.state.clientID} partyID={this.state.partyID} username={this.state.username} />
        }

        return (
            <div>
                <h1>Artifice</h1>
                {content}
            </div>
        );
    }
}

export default App;
