import React from 'react';

class JoinParty extends React.Component{
    constructor(props) {
        super(props);

        this.state({
            partyCode: '',
            username: ''
        })
    }
    
    joinParty(_joinGame) {

        // Swap Scene
        _joinGame();
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    render() {
        return (
            <div>

                <label>
                    Party Code:
                    <input type="text" name="partyCode" onChange={this.handleChange} />
                </label>
                
                <br/>

                <label>
                    Name:
                    <input type="text" name="name" onChange={this.handleChange} />
                </label>
                
                <br/>

                <button onClick={() => joinParty(this.props.joinGame)}>Join Party</button>

                <br/>

                <button onClick={this.props.backClick}>Back</button>
            </div>
        );
    }
}

export default JoinParty;
