import React from 'react';

class JoinParty extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            partyCode: '',
            username: '',
        }

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    errorMsgStyle = {
        margin: 0,
        color: 'red'
    }
    
    joinParty(_joinGame) {
        if(this.state.partyCode === '') {
            _joinGame({type: 'join', msg: 'party code is required'}, false, "", "");
            return;
        }

        if(this.state.username === '') {
            _joinGame({type: 'join', msg: 'username is required'}, false, "", "");
            return;
        }
        let isHost = false;
        _joinGame({type: 'none'}, isHost, this.state.username, this.state.partyCode);
    }

    handleInputChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value,
        });
    }

    render() {
        let inputError = '';
        console.log(this.props)
        if (this.props.joinErrorMsg !== '') {
            inputError = <p style={this.errorMsgStyle}>{this.props.joinErrorMsg}</p>
        }

        return (
            <div>
                <label>
                    Party Code:
                    <input type="text" name="partyCode" value={this.state.partyCode} onChange={this.handleInputChange} />
                </label>
                
                <br/>

                <label>
                    Name:
                    <input type="text" name="username" value={this.state.username} onChange={this.handleInputChange} />
                </label>
                
                <br/>

                {inputError}

                <button onClick={() => this.joinParty(this.props.joinGame)}>Join Party</button>

                <br/>

                <button onClick={this.props.backClick}>Back</button>
            </div>
        );
    }
}

export default JoinParty;
