import React, { Component} from "react";
import Home from "./home";
import Game from "./game";

class App extends Component {
    constructor() {
        super();
        this.state = {
            page: 'home'
        }
    }

    componentDidMount() {
        this.connectToParty();
    }

    joinGame() {
        this.setState({
            page: 'game'
        })
    }

    render() { 
        let content;
        if(this.state.page === 'home') {
            content = <Home joinGame={this.joinGame} />
        } else if(this.state.page === 'game') {
            content = <Game clientID={this.state.clientID} />
        }

        return (
            <div>
                <h1>Artifice</h1>
                {content}
            </div>
        );
    }
}

export default App;
