import React from 'react';
import {useEffect} from 'react';
import Players from './components/players';
import RoleInfo from './components/roleInfo';
import PhaseInfo from './components/phaseInfo';
import Notes from './components/notes';
import DemonChat from './components/demonChat';
import DefenseVote from './components/defenseVote';
import Logs from './components/logs';
import Winners from './components/winners';

function closeAll() {
    console.log('close all')

    useEffect(() => { 
        document.getElementById('info').style.height = 0;
        document.getElementById('notes').style.visibility = 'hidden';
        document.getElementById('logs').style.height = 'hidden';
        document.getElementById('demonChat').style.height = 'hidden';
    }, []);
}

function openNotes() {
    closeAll();
    document.getElementById('notes').style.height = '50vh';
    useEffect(() => { 
        
    }, []);
}

function openLogs() {
    closeAll();
    document.getElementById('logs').style.height = '50vh';
    useEffect(() => { 
        
    }, []);
}

function openDemonChat () {
    closeAll();
    document.getElementById('demonChat').style.height = '50vh';
    useEffect(() => { 
        
    }, []);
}

function Game(props) {

    return (
        <div>
            <div className='header'>
                <h1>{props.username}</h1>
            </div>
            
            <RoleInfo 
                playerInfo={props.playerInfo}
                gameStarted={props.gameStarted}
                partyCode={props.partyCode}
            />
            
            <PhaseInfo
                phase={props.phase}
                day={props.day}
                timer={props.timer}
                gameStarted={props.gameStarted}
                defendant={props.defendant}
                defenseOutcome={props.defenseOutcome}
                gameOver={props.gameOver}
                players={props.players}
                startGame={props.startGame}
                isHost={props.isHost}
            />
            
            <div className='info-header'>
                <button onClick={openNotes}>Notes</button>
                <button onClick={openLogs}>Logs</button>
                {props.gameStarted && props.playerInfo.team === 'demon' ? 
                    <button onClick={openDemonChat}>Demon Chat</button> : ''
                }
            </div>

            <div id='info' className='info'>
                {props.gameStarted ? <Notes/> : ''}
                <Logs logs={props.logs}/>
                {props.gameStarted && props.phase === 'night' && props.playerInfo.team === 'demon' ? 
                    <DemonChat 
                        demonChatMsgs={props.demonChatMsgs} demonChatMessage={props.demonChatMessage}
                    /> : ''
                }
            </div>

            {props.phase === 'defenseVote' && props.defendant !== props.username ? 
                <DefenseVote 
                    defenseVote={props.defenseVote}
                    gameOver={props.gameOver}
                /> : ''
            }

            <Winners 
                gameOver={props.gameOver}
                teamWinners={props.teamWinners}
                playerWinners={props.playerWinners}
                startGame={props.startGame}
                isHost={props.isHost}
            />
            <Players
                playerInfo={props.playerInfo}
                allPlayers={props.players}
                phase={props.phase}
                vote={props.vote}
                roleTarget={props.roleTarget}
                usedSelf={props.usedSelf}
            />
        </div>
    );
}
 
export default Game;
