import React from 'react';

function handleChatMessage(_demonChatMessage) {

}

function DemonChat(props) {

    const chatMsgs = props.demonChatMsgs.map((data) =>
        <div><span className='chatUser'>{data.username}: </span>data.message</div>
    );

    return (
        <div className='demonChat'>
            <div>
                {chatMsgs}
            </div>
            Demon Chat:
            <input className='demonChat' type='text' name="chatInput" />
            <button onClick={handleChatMessage(props.demonChatMessage)}>Send Message</button>
        </div>
    );
}

export default DemonChat;
