import React from 'react';

function PhaseInfo(props) {

    let phase = '';
    let day = '';
    let timer = '';

    if(props.gameStarted) {
        // Phase
        if(props.phase === 'defense') {
            phase=props.defendant + ", Defend Yourself!"
        } else if (props.phase === 'defenseVote') {
            phase = 'Vote to decicide ' + defendant + "'s fate."
        }
        else {
            phase = props.phase
        }

        // Day
        if (phase === 'night'){
            day = <p>{"Night " + props.day}</p>;
        } else {
            day = <p>{"Day " + props.day}</p>;
        }

        // Timer
        timer = <p>{"Time Left: " + props.timer}</p>           
    }

    const hideDiv = {
        display: 'none'
    }

    const showDiv = {
        display: 'block'
    }

    return (
        <div style={props.gameStarted ? showDiv : hideDiv}>
            <div>{phase}</div>
            {day}
            {timer}
        </div>
    );
}

export default PhaseInfo;
