import React, { Component } from 'react';
import Demons from './demons';

class DemonChat extends Component {


    render() {
        let demonchat = ''
        if(this.props.gameStarted && this.props.phase === 'night' && this.props.playerInfo.team === 'demon'){
            demonchat = <Demons 
                            demonChatMsgs={this.props.demonChatMsgs} 
                            demonChatMessage={this.props.demonChatMessage}
                            gameStarted={this.props.gameStarted}
                            phase={this.props.phase}
                            playerInfo={this.props.playerInfo}
                        />
        }

        return (
            {demonchat}
        );
    }
}

export default DemonChat;
