import React from 'react';

function PhaseInfo(props) {

    let info = '';
    if(props.isStarted) {
        // Phase
        phase = props.phase

        // Day
        if (phase === 'night'){
            day = "Night " + props.day;
        } else {
            day = "Day " + props.day;
        }

        // Timer
        timer = "Time Left: " + props.timer

        info = <div>
                    {phase}
                    {day}
                    {timer}
                </div>
    }

    return (
        {info}
    );
}

export default PhaseInfo;
