import React from 'react';

class JoinParty extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            partyCode: '',
            username: '',
            errorMsg: ''
        }

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    errorMsgStyle = {
        margin: 0,
        color: 'red'
    }
    
    joinParty(_joinGame) {
        if(this.state.partyCode === '') {
            this.setState({
                errorMsg: "party code is required"
            });
            return;
        }

        if(this.state.username === '') {
            this.setState({
                errorMsg: "username is required"
            });
            return;
        }
        let isHost = false;
        _joinGame(isHost, this.state.username, this.state.partyCode);
    }

    handleInputChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }

    render() {
        let inputError = '';
        if (this.state.errorMsg !== '') {
            inputError = <p style={this.errorMsgStyle}>{this.state.errorMsg}</p>
        }

        return (
            <div>
                <label>
                    Party Code:
                    <input type="text" name="partyCode" value={this.state.partyCode} onChange={this.handleInputChange} />
                </label>
                
                <br/>

                <label>
                    Name:
                    <input type="text" name="username" value={this.state.username} onChange={this.handleInputChange} />
                </label>
                
                <br/>

                {inputError}

                <button onClick={() => this.joinParty(this.props.joinGame)}>Join Party</button>

                <br/>

                <button onClick={this.props.backClick}>Back</button>
            </div>
        );
    }
}

export default JoinParty;
