import React, { Component } from 'react';
import Players from './components/players';
import RoleInfo from './components/roleInfo';
import PhaseInfo from './components/phaseInfo';
import Notes from './components/notes';
import DemonChat from './components/demonChat';
import DefenseVote from './components/defenseVote';
import Logs from './components/logs';
import Winners from './components/winners';

class Game extends Component {
    constructor() {
        super();
        this.state = {
            infoHeight: 0,
            notes: 'hidden',
            logs: 'hidden',
            demonChat: 'hidden'
        }

        this.closeAll = this.closeAll.bind(this);
        this.openNotes = this.openNotes.bind(this);
        this.openLogs = this.openLogs.bind(this);
        this.openDemonChat = this.openDemonChat.bind(this);
    }
    
    closeAll() {
        if(!this.gameStarted) return;
        this.setState ({
            infoHeight: 0,
            notes: 'hidden',
            logs: 'hidden',
            demonChat: 'hidden'
        })
    }

    openNotes() {
        if(!this.gameStarted) return;
        this.closeAll();
        this.setState ({
            infoHeight: '50vh',
            notes: 'visible',
        })
    }
    
    openLogs() {
        if(!this.gameStarted) return;
        this.closeAll();
        this.setState ({
            infoHeight: '50vh',
            logs: 'visible',
        })
    }
    
    openDemonChat () {
        if(!this.gameStarted) return;
        this.closeAll();
        this.setState ({
            infoHeight: '50vh',
            demonChat: 'visible',
        })
    }

    render() {
        return (
            <div>
                <div className='header'>
                    <h1>{this.props.username}</h1>
                </div>
                
                <RoleInfo 
                    playerInfo={this.props.playerInfo}
                    gameStarted={this.props.gameStarted}
                    partyCode={this.props.partyCode}
                />
                
                <PhaseInfo
                    phase={this.props.phase}
                    day={this.props.day}
                    timer={this.props.timer}
                    gameStarted={this.props.gameStarted}
                    defendant={this.props.defendant}
                    defenseOutcome={this.props.defenseOutcome}
                    gameOver={this.props.gameOver}
                    players={this.props.players}
                    startGame={this.props.startGame}
                    isHost={this.props.isHost}
                />
                
                <div className='info-header'>
                    <button onClick={this.openNotes}>Notes</button>
                    <button onClick={this.openLogs} visibility={this.state.hidden}>Logs</button>
                    {this.props.gameStarted && this.props.playerInfo.team === 'demon' ? 
                        <button onClick={this.openDemonChat} visibility={this.state.demonChat}>Demon Chat</button> : ''
                    }
                </div>

                <div id='info' className='info' style={{height: this.state.infoHeight}}>
                    {this.props.gameStarted ? <Notes visible={this.state.notes}/> : ''}
                    <Logs logs={this.props.logs} visible={this.state.logs}/>
                    {this.props.gameStarted && this.props.phase === 'night' && this.props.playerInfo.team === 'demon' ? 
                        <DemonChat 
                            demonChatMsgs={this.props.demonChatMsgs} demonChatMessage={this.props.demonChatMessage} visible={this.state.demonChat}
                        /> : ''
                    }
                </div>

                {this.props.phase === 'defenseVote' && this.props.defendant !== this.props.username ? 
                    <DefenseVote 
                        defenseVote={this.props.defenseVote}
                        gameOver={this.props.gameOver}
                    /> : ''
                }

                <Winners 
                    gameOver={this.props.gameOver}
                    teamWinners={this.props.teamWinners}
                    playerWinners={this.props.playerWinners}
                    startGame={this.props.startGame}
                    isHost={this.props.isHost}
                />
                <Players
                    playerInfo={this.props.playerInfo}
                    allPlayers={this.props.players}
                    phase={this.props.phase}
                    vote={this.props.vote}
                    roleTarget={this.props.roleTarget}
                    usedSelf={this.props.usedSelf}
                />
            </div>
        );
    }
}
 
export default Game;
