import React from 'react';

function RoleInfo(props) {
    let playerInfo = props.playerInfo
    let role ='';
    let roleDescription = '';

    if(playerInfo.role !== '') {
        role = <h1>{playerInfo.role}</h1>
        roleDescription = <h2>{playerInfo.roleDescription}</h2>
    }
    
    return (
        <div>
            {role}
            {roleDescription}
        </div>
    );
}

export default RoleInfo;
