import React from 'react';

class CreateParty extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            username: '',
            errorMsg: ''
        };

        this.handleInputChange = this.handleInputChange.bind(this);
    }
    
    createParty(_joinGame) {
        if(this.state.username === '') this.setState({errorMsg: "username is required"});
        let isHost = true;
        _joinGame(isHost, this.state.username, '');
    }

    handleInputChange(event) {
        this.setState({
            username: event.target.value
        });
    }

    render() {
        let inputError = '';
        if (this.state.errorMsg !== '') {
            inputError = <p style={{margin: 0}}>{this.state.errorMsg}</p>
        }
        return (
            <div>
                <label>
                    Name:
                    <input type="text" name="username" value={this.state.username} onChange={this.handleInputChange} />
                </label> <br/>
                {inputError}
                <button onClick={() => this.createParty(this.props.joinGame)}>Create Party</button> <br/>
                <button onClick={this.props.backClick}>Back</button>
            </div>
        );
    }
}

export default CreateParty;
