import React, { Component} from "react";
import './App.css';
import Home from "./home";
import Game from "./game";

class App extends Component {
    constructor() {
        super();
        this.state = {
            page: 'home',
            joinError: '',
            clientID: '',
            partyCode: '',
            username: '',
            allPlayers: [],
            playerInfo: {
                isDead: false,
                role: '',
                team: ''
            }
        }
    }

    joinGame(err, isHost, username, partyCode) {
        if(err.type === 'join') {
            this.setState({
                joinError: err.msg
            })
            return
        }
        let ws = new WebSocket("ws://localhost:8000");

        ws.onmessage = message => {
            const response = JSON.parse(message.data);

            // Connected to server
            if(response.method === "connect") {
                // Save generated client ID
                this.setState({
                    clientID: response.clientID
                })

                // Send the room connection request to the server
                let payLoad;
                if(isHost) {
                    // Host Party
                    payLoad = {
                        method: 'host',
                        clientID: response.clientID,
                        username: username
                    }
                } else {
                    // Join Party
                    payLoad = {
                        method: 'join',
                        clientID: response.clientID,
                        partyCode: partyCode,
                        username: username
                    }
                }
                ws.send(JSON.stringify(payLoad));
            }

            // Joined Game Party
            else if(response.method === "partyConnect") {
                // Update State
                this.setState({
                    page: 'game',
                    partyCode: response.partyCode,
                    username: username,
                    allPlayers: response.players
                })
            }

            // Party Join Error
            else if(response.method === "partyJoinError") {
                this.setState({
                    joinError: response.errorMsg
                })
            }

            else if(response.method === "updatePlayerList") {
                this.setState({
                    allPlayers: response.players
                })
            }
        }
    }

    render() { 
        let content;
        if(this.state.page === 'home') {
            content = <Home 
                        joinGame={(err, isHost, partyCode, username) => this.joinGame(err, isHost, partyCode, username)} 
                        joinErrMsg={this.state.joinError}
                    />
        } else if(this.state.page === 'game') {
            content = <Game 
                        partyCode={this.state.partyCode}
                        username={this.state.username}
                        players={this.state.allPlayers} 
                    />
        }

        return (
            <div>
                {content}
            </div>
        );
    }
}

export default App;
