import React from 'react';

function RoleInfo(props) {
    let playerInfo = props.playerInfo
    let role ='';
    let roleDescription = '';
    let code ='';
    let startButton = '';

    if(!props.gameStarted) {
        code = <h2>Party Code: {props.partyCode}</h2>
        if(props.isHost && props.players.length >= 3) {
            startButton = <button className='start-button' onClick={props.startGame}>Start Game</button>
        }
    }

    if(playerInfo.role !== '') {
        role = <h1>{playerInfo.role}</h1>
    }
    
    return (
        <div className='role-header'>
            {code}
            {startButton}
            {role}
            {roleDescription}
        </div>
    );
}

export default RoleInfo;
