import React from 'react';

class CreateParty extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            username: '',
            errorMsg: ''
        };

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    errorMsgStyle = {
        margin: 0,
        color: 'red'
    }
    
    createParty(_joinGame) {
        let inputName = this.state.username.trim();
        if(inputName === '') {
            this.setState({errorMsg: "username is required"});
            return;
        }
        let isHost = true;
        _joinGame({type: 'none'}, isHost, inputName, '');
    }

    handleInputChange(event) {
        this.setState({
            username: event.target.value,
            errorMsg: ''
        });
    }

    render() {
        let inputError = '';
        if (this.state.errorMsg !== '') {
            inputError = <p style={this.errorMsgStyle}>{this.state.errorMsg}</p>
        }
        return (
            <div>
                <label>
                    Name:
                    <input 
                        type="text" 
                        name="username" 
                        value={this.state.username} 
                        onChange={this.handleInputChange}
                        autocomplete="off"
                    />
                </label> 
                
                <br/>

                {inputError}

                <button onClick={() => this.createParty(this.props.joinGame)}>Create Party</button> 
                
                <br/>

                <button onClick={this.props.backClick}>Back</button>
            </div>
        );
    }
}

export default CreateParty;
