import React, { Component} from "react";
import Home from "./home";
import Game from "./game";

class App extends Component {
    constructor() {
        super();
        this.state = {
            page: 'home',
            clientID: '',
            partyID: '',
            username: '',
        }
    }

    joinGame(clientID, partyID, username) {
        this.setState({
            page: 'game',
            clientID: clientID,
            partyID: partyID,
            username: username
        })
    }

    render() { 
        let content;
        if(this.state.page === 'home') {
            content = <Home joinGame={(clientID, partyID) => this.joinGame(clientID, partyID)} />
        } else if(this.state.page === 'game') {
            content = <Game clientID={this.state.clientID} partyID={this.state.partyID} username={this.state.username} />
        }

        return (
            <div>
                <h1>Artifice</h1>
                {content}
            </div>
        );
    }
}

export default App;
