import React from 'react';

class JoinParty extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            partyCode: '',
            username: ''
        }
    }
    
    joinParty(_joinGame) {

        // Swap Scene
        _joinGame();
    }

    handlePartyCodeChange(event) {
        this.setState({
            partyCode: event.target.value
        });
    }

    handlePartyUsernameChange(event) {
        this.setState({
            username: event.target.value
        });
    }

    render() {
        return (
            <div>
                <label>
                    Party Code:
                    <input type="text" name="partyCode" value={this.state.partyCode} onChange={() => this.handlePartyCodeChange} />
                </label>
                
                <br/>

                <label>
                    Name:
                    <input type="text" name="name" value={this.state.username} onChange={() => this.handleUsernameChange} />
                </label>
                
                <br/>

                <button onClick={() => this.joinParty(this.props.joinGame)}>Join Party</button>

                <br/>

                <button onClick={this.props.backClick}>Back</button>
            </div>
        );
    }
}

export default JoinParty;
