import React from 'react';

function DefenseVote(props) {
    let yesButton = '';
    let noButton = '';
    if(!props.gameOver){
        yesButton = <button className='Yes' onClick={() => props.defenseVote('yes')} >Yes</button>
        noButton = <button onClick={() => props.defenseVote('no')} >No</button>
    }
    return (
        <div className='defense-buttons'>
            {!props.gameOver ? 'Should ' + this.props.defendant + ' Die?' : ''}
            {yesButton}
            {noButton}
        </div>
    );
}

export default DefenseVote;
