import React from 'react';

const normalStyle = {
    color: 'black'
}

const demonStyle = {
    color: 'red'
}

const deadStyle = {
    color: 'grey'
}

function handleVote(_vote, voteTarget) {
    _vote(voteTarget);
}

function playerStyle(player) {
    if(player.isDead) {
        return deadStyle;
    } else if (player.who === 'demon') {
        return demonStyle;
    }

    return normalStyle
}

function Players(props) {

    const allPlayers = props.allPlayers
    const playerList = allPlayers.map((player, i) =>
        <div key={i+1} >
            {(i+1) + ". " + player.username}
            {props.phase === 'voting' ? <span>{'votes: ' + player.votes} </span> : ''}
            {props.phase === 'voting' && !player.isSelf ? <button onClick={() => handleVote(props.vote, player.username)} > Vote</button> : ''}
        </div>
    );

    return (
        <div>
            {playerList}
        </div>
    );
}

export default Players;
