import React, { Component} from "react";
import Home from "./home";
import Game from "./game";

class App extends Component {
    constructor() {
        super();
        this.state = {
            page: 'home',
            clientID: '',
            partyID: ''
        }
    }

    componentDidMount() {
        this.connectToParty();
    }

    joinGame(clientID, partyID) {
        this.setState({
            page: 'game',
            clientID: clientID,
            partyID, partyID
        })
    }

    render() { 
        let content;
        if(this.state.page === 'home') {
            content = <Home joinGame={this.joinGame} />
        } else if(this.state.page === 'game') {
            content = <Game clientID={this.state.clientID} partyID={this.state.partyID} />
        }

        return (
            <div>
                <h1>Artifice</h1>
                {content}
            </div>
        );
    }
}

export default App;
