import React from 'react';

function PhaseInfo(props) {
    // Phase
    let phase = props.phase

    // Day
    let day;
    if (phase === 'night'){
        day = "Night " + props.day;
    } else {
        day = "Day " + props.day;
    }

    // Timer
    let timer = "Time Left: " + props.timer

    return (
        <div>
            {phase}
            {day}
            {timer}
        </div>
    );
}

export default PhaseInfo;
