import React from 'react';

function PhaseInfo(props) {

    let phase = '';
    let day = '';
    let timer = '';

    if(props.isStarted) {
        // Phase
        phase = props.phase

        // Day
        day;
        if (phase === 'night'){
            day = "Night " + props.day;
        } else {
            day = "Day " + props.day;
        }

        // Timer
        timer = "Time Left: " + props.timer              
    }

    return (
        <div>
            {phase}
            {day}
            {timer}
        </div>
    );
}

export default PhaseInfo;
