import React, { Component } from 'react';

class DemonChat extends Component {
    constructor(props) {
        super(props);

        this.state = {
            chatInput: '',
            msgError: '',
        }

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleChatMessage(_demonChatMessage) {
        let message = this.state.chatInput.trim();

        // check if message is empty
        if(message === '') return;

        // check if message is too long

        _demonChatMessage(this.state.chatInput);
    }

    handleInputChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value,
        });
    }

    render() {
        const chatMsgs = this.props.demonChatMsgs.map((data) =>
            <div>
                <span className='chatUser'>{data.username}: </span>data.message
            </div>
        );

        return (
            <div className='demonChat'>
                <div>
                    {chatMsgs}
                </div>
                Demon Chat:
                <input 
                    className='demonChat' 
                    type='text' 
                    name="chatInput"
                    onChange={this.handleInputChange}
                />
                <button onClick={this.handleChatMessage(this.props.demonChatMessage)}>Send Message</button>
            </div>
        );
    }
}

export default DemonChat;
