import React, { Component } from 'react';
import Demons from './demons';

class DemonChat extends Component {
    constructor(props) {
        super(props);

        this.state = {
            chatInput: '',
            msgError: '',
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleChatMessage = this.handleChatMessage.bind(this);
    }

    handleChatMessage(_demonChatMessage) {
        let message = this.state.chatInput.trim();

        // check if message is empty
        if(message === '') return;

        _demonChatMessage(this.state.chatInput);
        this.setState({
            chatInput: ''
        })
    }

    handleInputChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value,
        });
    }

    render() {
        let demonchat = ''
        if(this.props.gameStarted && this.props.phase === 'night' && this.props.playerInfo.team === 'demon'){
            demonchat = <Demons 
                            demonChatMsgs={this.props.demonChatMsgs} 
                            demonChatMessage={this.props.demonChatMessage}
                            gameStarted={this.props.gameStarted}
                            phase={this.props.phase}
                            playerInfo={this.props.playerInfo}
                        />
        }

        return (
            {demonchat}
        );
    }
}

export default DemonChat;
