import React from 'react';

function handleVote(_vote, voteTarget) {
    _vote(voteTarget);
}

function Players(props) {
    const normalStyle = {
        color: 'black'
    }

    const demonStyle = {
        color: 'red'
    }

    const allPlayers = props.allPlayers
    const playerList = allPlayers.map((player, i) =>
        <div key={i+1} 
            style={player.who === 'demon' ? demonStyle : normalStyle}
        >
            {(i+1) + ". " + player.username}
            {props.phase === 'voting' ? 'votes: ' + player.votes : ''}
            {props.phase === 'voting' && !player.isSelf ? <button onClick={() => handleVote(props.vote, player.username)} > Vote</button> : ''}
        </div>
    );

    return (
        <div>
            {playerList}
        </div>
    );
}

export default Players;
