import React, { Component } from 'react';

class DemonChat extends Component {
    constructor(props) {
        super(props);

        this.state = {
            chatInput: '',
            msgError: '',
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleChatMessage = this.handleChatMessage.bind(this);
    }

    handleChatMessage(_demonChatMessage) {
        let message = this.state.chatInput.trim();

        // check if message is empty
        if(message === '') return;

        _demonChatMessage(this.state.chatInput);
        this.setState({
            chatInput: ''
        })
    }

    handleInputChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value,
        });
    }

    render() {
        const chatMsgs = this.props.demonChatMsgs.map((data, i) =>
            <div key={i}>
                <span className='chatUser'>{data.username}: </span>{data.message}
            </div>
        );

        return (
            <div 
                className='demonChat' 
                style={{
                    visibility: this.props.visible,
                    height: this.props.height,
                }}>
                <div>
                    {chatMsgs}
                </div>
                Demon Chat:
                {this.props.phase === 'night' ? <input
                    type='text' 
                    name="chatInput"
                    value={this.state.chatInput} 
                    onChange={this.handleInputChange}
                /> : '' }
                {this.props.phase === 'night' ? <button id='demonMsg' onClick={() => this.handleChatMessage(this.props.demonChatMessage)}>Send Message</button> : ''}
            </div>
        );
    }
}

export default DemonChat;
