import React from 'react';

function Demons() {
    return (
        <div className='demonChat'>
            <div>
                {chatMsgs}
            </div>
            Demon Chat:
            <input 
                className='demonChat' 
                type='text' 
                name="chatInput"
                value={this.state.chatInput} 
                onChange={this.handleInputChange}
            />
            <button id='demonMsg' onClick={() => this.handleChatMessage(this.props.demonChatMessage)}>Send Message</button>
        </div>
    );
}

export default Demons;
