import React, { Component } from 'react';
import Demons from './demons';

class DemonChat extends Component {
    constructor(props) {
        super(props);

        this.state = {
            chatInput: '',
            msgError: '',
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleChatMessage = this.handleChatMessage.bind(this);
    }

    handleChatMessage(_demonChatMessage) {
        let message = this.state.chatInput.trim();

        // check if message is empty
        if(message === '') return;

        _demonChatMessage(this.state.chatInput);
        this.setState({
            chatInput: ''
        })
    }

    handleInputChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value,
        });
    }

    render() {
        const chatMsgs = this.props.demonChatMsgs.map((data, i) =>
            <div key={i}>
                <span className='chatUser'>{data.username}: </span>{data.message}
            </div>
        );

        let demonchat = ''
        if(this.props.gameStarted && this.props.phase === 'night' && this.props.playerInfo.team === 'demon'){
            demonchat = <Demons />
        }

        return (
            {demonchat}
        );
    }
}

export default DemonChat;
