import React from 'react';

const normalStyle = {
    color: 'black'
}

const demonStyle = {
    color: 'red'
}

const deadStyle = {
    color: 'grey'
}

function handleVote(_vote, voteTarget) {
    _vote(voteTarget);
}

function handlePsychic(_psychicTarget, voteTarget) {
    _psychicTarget(voteTarget); 
}

function handleDemon(_demonTarget, voteTarget) {
    _demonTarget(voteTarget); 
}

function playerStyle(player) {
    if(player.isDead) {
        return deadStyle;
    } else if (player.who === 'demon') {
        return demonStyle;
    }

    return normalStyle
}

function getVoteButton(props, player) {
    if(props.phase === 'voting' && !player.isSelf) {
        return <button 
                    onClick={() => handleVote(props.vote, player.username)} 
                > Vote
                </button>;
    } else if(props.playerInfo.role === 'Psychic' && props.phase === 'night' && !player.isSelf) {
        console.log()
        return <button 
                    onClick={() => handlePsychic(props.psychicTarget, player.username)} 
                > Soul Read
                </button>;
    }

    else if(props.playerInfo.role === 'Demon' && props.phase === 'night' && !player.isSelf) {
        console.log()
        return <button 
                    onClick={() => handleDemon(props.demonTarget, player.username)} 
                > Kill
                </button>;
    }
}

function Players(props) {

    const allPlayers = props.allPlayers
    const playerList = allPlayers.map((player, i) =>
        <div key={i+1} style={playerStyle(player)}>
            {(i+1) + ". " + player.username}
            {props.phase === 'voting' ? <span>{'votes: ' + player.votes} </span> : ''}
            {getVoteButton(props, player)}
        </div>
    );

    return (
        <div>
            {playerList}
        </div>
    );
}

export default Players;
